<div class="min-h-screen bg-gray-100 p-8">
  <div class="max-w-4xl mx-auto bg-white rounded-lg shadow-lg p-6">
    <div class="flex justify-between items-center mb-6">
      <h2 class="text-3xl font-bold text-gray-800">Welcome, {{ currentUser()?.name }}!</h2>
      <button
        (click)="onLogout()"
        class="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
      >
        Logout
      </button>
    </div>

    <div class="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
      <!-- Current Balance -->
      <div class="bg-blue-500 text-white p-6 rounded-lg shadow-md">
        <h3 class="text-xl font-semibold mb-2">Current Balance</h3>
        <p class="text-4xl font-bold">
          {{ currentUser()?.balance | currency:'USD':'symbol':'1.2-2' }}
        </p>
      </div>

      <!-- Deposit Section -->
      <div class="bg-green-100 p-6 rounded-lg shadow-md">
        <h3 class="text-xl font-semibold mb-4 text-green-800">Deposit Funds</h3>
        <div class="flex gap-4">
          <input
            type="number"
            [(ngModel)]="depositAmount"
            placeholder="Amount"
            class="flex-grow shadow appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            min="0.01"
            step="0.01"
          />
          <button
            (click)="onDeposit()"
            class="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Deposit
          </button>
        </div>
      </div>

      <!-- Withdrawal Section -->
      <div class="bg-red-100 p-6 rounded-lg shadow-md">
        <h3 class="text-xl font-semibold mb-4 text-red-800">Withdraw Funds</h3>
        <div class="flex gap-4">
          <input
            type="number"
            [(ngModel)]="withdrawAmount"
            placeholder="Amount"
            class="flex-grow shadow appearance-none border rounded py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            min="0.01"
            step="0.01"
          />
          <button
            (click)="onWithdraw()"
            class="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Withdraw
          </button>
        </div>
      </div>
    </div>

    <!-- Transaction History -->
    <div class="bg-gray-50 p-6 rounded-lg shadow-md">
      <h3 class="text-2xl font-bold mb-4 text-gray-800">Transaction History</h3>
      <div *ngIf="userTransactions().length === 0" class="text-gray-600">No transactions yet.</div>
      <ul class="divide-y divide-gray-200">
        <li *ngFor="let transaction of userTransactions()" class="py-3 flex justify-between items-center">
          <div>
            <span class="font-semibold capitalize">{{ transaction.type }}</span>
            <span class="text-sm text-gray-500 ml-2">({{ transaction.date | date:'short' }})</span>
          </div>
          <span
            [ngClass]="{
              'text-green-600': transaction.type === 'deposit',
              'text-red-600': transaction.type === 'withdrawal'
            }"
            class="font-bold text-lg"
          >
            {{ transaction.type === 'deposit' ? '+' : '-' }}{{ transaction.amount | currency:'USD':'symbol':'1.2-2' }}
          </span>
        </li>
      </ul>
    </div>
  </div>
</div>
