          <div class="bg-white p-6 rounded-xl shadow-md">
            <h3 class="text-xl font-semibold mb-4 text-slate-700">Deposit</h3>
            <div class="flex gap-3">
              <input type="number" [(ngModel)]="depositAmount" placeholder="Amount" class="flex-grow w-full px-3 py-2 bg-slate-50 border border-slate-200 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 transition-shadow" />
              <button (click)="onDeposit()" class="px-5 py-2 bg-green-500 text-white font-semibold rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transition-transform active:scale-95">
                Go
              </button>
            </div>
          </div>
          <!-- Withdraw Card -->
          <div class="bg-white p-6 rounded-xl shadow-md">
            <h3 class="text-xl font-semibold mb-4 text-slate-700">Withdraw</h3>
            <div class="flex gap-3">
              <input type="number" [(ngModel)]="withdrawAmount" placeholder="Amount" class="flex-grow w-full px-3 py-2 bg-slate-50 border border-slate-200 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 transition-shadow" />
              <button (click)="onWithdraw()" class="px-5 py-2 bg-red-500 text-white font-semibold rounded-md hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 transition-transform active:scale-95">
                Go
              </button>
            </div>
          </div>
        </div>
      </div>

      <!-- Right Column: Transaction History -->
      <div class="lg:col-span-1 bg-white p-6 rounded-xl shadow-md">
        <h3 class="text-2xl font-bold mb-6 text-slate-800">History</h3>
        <div *ngIf="userTransactions().length === 0" class="text-center py-10">
          <svg xmlns="http://www.w3.org/2000/svg" class="mx-auto h-12 w-12 text-slate-400" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="1.5">
            <path stroke-linecap="round" stroke-linejoin="round" d="M4 6h16M4 10h16M4 14h16M4 18h16" />
          </svg>
          <p class="mt-4 text-slate-500">No transactions recorded yet.</p>
        </div>
        <ul *ngIf="userTransactions().length > 0" class="space-y-4">
          <li *ngFor="let transaction of userTransactions()" class="flex items-center justify-between p-3 bg-slate-50 rounded-lg">
            <div class="flex items-center space-x-3">
              <div class="p-2 rounded-full" [ngClass]="{'bg-green-100': transaction.type === 'deposit', 'bg-red-100': transaction.type === 'withdrawal'}">
                <svg *ngIf="transaction.type === 'deposit'" xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 text-green-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                  <path stroke-linecap="round" stroke-linejoin="round" d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                </svg>
                <svg *ngIf="transaction.type === 'withdrawal'" xmlns="http://www.w3.org/2000/svg" class="h-6 w-6 text-red-600" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
                  <path stroke-linecap="round" stroke-linejoin="round" d="M18 12H6" />
                </svg>
              </div>
              <div>
                <p class="font-semibold text-slate-700 capitalize">{{ transaction.type }}</p>
                <p class="text-sm text-slate-500">{{ transaction.date | date:'short' }}</p>
              </div>
            </div>
            <p class="font-bold text-lg" [ngClass]="{'text-green-600': transaction.type === 'deposit', 'text-red-600': transaction.type === 'withdrawal'}">
              {{ transaction.type === 'deposit' ? '+' : '-' }}{{ transaction.amount | currency:'USD':'':'1.2-2' }}
            </p>
          </li>
        </ul>
      </div>
    </div>
  </main>
</div>
